'        l_name_attr := '' name="'' || l_name || ''"'';',
'    END IF;',
'',
'    -- gets the display value it has on load based on param value',
'    IF p_param.value IS NOT NULL THEN',
'        l_display_value := apex_plugin_util.get_display_data(',
